            Assert.AreEqual(expected, given.repr);
            given = cp.ones_like(x);
            expected =
                "array([[1, 1, 1],\n" +
                "       [1, 1, 1]])";
            Assert.AreEqual(expected, given.repr);

            // >>> y = cp.arange(3, dtype=float)
            // >>> y
            // array([ 0.,  1.,  2.])
            // >>> cp.ones_like(y)
            // array([ 1.,  1.,  1.])
            // 

            var y = cp.arange(3, dtype: cp.float_);
            given = y;
            expected =
                "array([0., 1., 2.])";
            Assert.AreEqual(expected, given.repr);
            given = cp.ones_like(y);
            expected =
                "array([1., 1., 1.])";
            Assert.AreEqual(expected, given.repr);
        }

