        [TestMethod]
        public void asarrayTest()
        {
            // Convert a list into an array:

            // >>> a = [1, 2]
            // >>> cp.asarray(a)
            // array([1, 2])
            // 

#if TODO
            var given = a = [1, 2];
             given = cp.asarray(a);
            var expected =
                "array([1, 2])";
            Assert.AreEqual(expected, given.repr);
#endif
            // Existing arrays are not copied:

            // >>> a = cp.array([1, 2])
            // >>> cp.asarray(a) is a
            // True
            // 

#if TODO
             given = a = cp.array({1, 2});
             given = cp.asarray(a) is a;
             expected =
                "True";
            Assert.AreEqual(expected, given.repr);
#endif
            // If dtype is set, array is copied only if dtype does not match:

            // >>> a = cp.array([1, 2], dtype=cp.float32)
            // >>> cp.asarray(a, dtype=cp.float32) is a
            // True
            // >>> cp.asarray(a, dtype=cp.float64) is a
            // False
            // 

#if TODO
             given = a = cp.array({1, 2}, dtype = cp.float32);
             given = cp.asarray(a, dtype = cp.float32) is a;
             expected =
                "True";
            Assert.AreEqual(expected, given.repr);
             given = cp.asarray(a, dtype = cp.float64) is a;
             expected =
                "False";
            Assert.AreEqual(expected, given.repr);
#endif
            // Contrary to asanyarray, ndarray subclasses are not passed through:

            // >>> issubclass(cp.recarray, cp.ndarray)
            // True
            // >>> a = cp.array([(1.0, 2), (3.0, 4)], dtype='f4,i4').view(cp.recarray)
            // >>> cp.asarray(a) is a
            // False
            // >>> cp.asanyarray(a) is a
            // True
            // 

#if TODO
             given = issubclass(cp.recarray, cp.ndarray);
             expected =
                "True";
            Assert.AreEqual(expected, given.repr);
             given = a = cp.array({(1.0, 2), (3.0, 4)}, dtype = 'f4,i4').view(cp.recarray);
             given = cp.asarray(a) is a;
             expected =
                "False";
            Assert.AreEqual(expected, given.repr);
             given = cp.asanyarray(a) is a;
             expected =
                "True";
            Assert.AreEqual(expected, given.repr);
#endif
        }


