        [TestMethod]
        public void loadtxtTest()
        {
            // >>> from io import StringIO   # StringIO behaves like a file object
            // >>> c = StringIO(u"0 1\n2 3")
            // >>> cp.loadtxt(c)
            // array([[ 0.,  1.],
            //        [ 2.,  3.]])
            // 

            var tempFile = Path.GetTempFileName();
            File.WriteAllText(tempFile, "0 1\n2 3");
            var given = cp.loadtxt(tempFile);
            var expected =
                "array([[0., 1.],\n" +
                "       [2., 3.]])";
            Assert.AreEqual(expected, given.repr);


            // >>> c = StringIO(u"1,0,2\n3,0,4")
            // >>> x, y = cp.loadtxt(c, delimiter=',', usecols=(0, 2), unpack=True)
            // >>> x
            // array([ 1.,  3.])
            // >>> y
            // array([ 2.,  4.])
            // 

            File.WriteAllText(tempFile, "1,0,2\n3,0,4");
            var a = cp.loadtxt(tempFile, delimiter: ",", usecols: new[] { 0, 2 }, unpack: true);
            given = a[0];
            expected =
                "array([1., 3.])";
            Assert.AreEqual(expected, given.repr);
            given = a[1];
            expected =
                "array([2., 4.])";
            Assert.AreEqual(expected, given.repr);
        }


