        [TestMethod]
        public void linspaceTest()
        {
            // >>> cp.linspace(2.0, 3.0, num=5)
            // array([ 2.  ,  2.25,  2.5 ,  2.75,  3.  ])
            // >>> cp.linspace(2.0, 3.0, num=5, endpoint=False)
            // array([ 2. ,  2.2,  2.4,  2.6,  2.8])
            // >>> cp.linspace(2.0, 3.0, num=5, retstep=True)
            // (array([ 2.  ,  2.25,  2.5 ,  2.75,  3.  ]), 0.25)
            // 

            var given = cp.linspace(2.0, 3.0, 5);
            var expected =
                "array([2.  , 2.25, 2.5 , 2.75, 3.  ])";
            Assert.AreEqual(expected, given.repr);
            given = cp.linspace(2.0, 3.0, 5, false);
            expected =
                "array([2. , 2.2, 2.4, 2.6, 2.8])";
            Assert.AreEqual(expected, given.repr);
            given = cp.linspace(2, 3, out var step, 5, true);
            expected =
                "array([2.  , 2.25, 2.5 , 2.75, 3.  ])";
            Assert.AreEqual(expected, given.repr);
            Assert.AreEqual(step, 0.25);
        }


