        [TestMethod]
        public void meshgridTest()
        {
            // >>> nx, ny = (3, 2)
            // >>> x = cp.linspace(0, 1, nx)
            // >>> y = cp.linspace(0, 1, ny)
            // >>> xv, yv = cp.meshgrid(x, y)
            // >>> xv
            // array([[ 0. ,  0.5,  1. ],
            //        [ 0. ,  0.5,  1. ]])
            // >>> yv
            // array([[ 0.,  0.,  0.],
            //        [ 1.,  1.,  1.]])
            // >>> xv, yv = cp.meshgrid(x, y, sparse=True)  # make sparse output arrays
            // >>> xv
            // array([[ 0. ,  0.5,  1. ]])
            // >>> yv
            // array([[ 0.],
            //        [ 1.]])
            // 
            var (nx, ny) = (3, 2);
            var x = cp.linspace(0, 1, nx);
            var y = cp.linspace(0, 1, ny);
            var xv_yv = cp.meshgrid(x, y);
            var given = xv_yv[0];
            var expected =
                "array([[0. , 0.5, 1. ],\n" +
                "       [0. , 0.5, 1. ]])";
            Assert.AreEqual(expected, given.repr);
            given = xv_yv[1];
            expected =
                "array([[0., 0., 0.],\n" +
                "       [1., 1., 1.]])";
            Assert.AreEqual(expected, given.repr);
            xv_yv = cp.meshgrid(new[] { x, y }, sparse: true); // make sparse output arrays;
            given = xv_yv[0];
            expected =
                "array([[0. , 0.5, 1. ]])";
            Assert.AreEqual(expected, given.repr);
            given = xv_yv[1];
            expected =
                "array([[0.],\n" +
                "       [1.]])";
            Assert.AreEqual(expected, given.repr);
        }


