        [TestMethod]
        public void vanderTest()
        {
            // >>> x = cp.array([1, 2, 3, 5])
            // >>> N = 3
            // >>> cp.vander(x, N)
            // array([[ 1,  1,  1],
            //        [ 4,  2,  1],
            //        [ 9,  3,  1],
            //        [25,  5,  1]])
            // 

#if TODO
            var given = x = cp.array({1, 2, 3, 5});
             given = N = 3;
             given = cp.vander(x, N);
            var expected =
                "array([[ 1,  1,  1],\n" +
                "       [ 4,  2,  1],\n" +
                "       [ 9,  3,  1],\n" +
                "       [25,  5,  1]])";
            Assert.AreEqual(expected, given.repr);
#endif
            // >>> cp.column_stack([x**(N-1-i) for i in range(N)])
            // array([[ 1,  1,  1],
            //        [ 4,  2,  1],
            //        [ 9,  3,  1],
            //        [25,  5,  1]])
            // 

#if TODO
             given = cp.column_stack({x**(N-1-i) for i in range(N)});
             expected =
                "array([[ 1,  1,  1],\n" +
                "       [ 4,  2,  1],\n" +
                "       [ 9,  3,  1],\n" +
                "       [25,  5,  1]])";
            Assert.AreEqual(expected, given.repr);
#endif
            // >>> x = cp.array([1, 2, 3, 5])
            // >>> cp.vander(x)
            // array([[  1,   1,   1,   1],
            //        [  8,   4,   2,   1],
            //        [ 27,   9,   3,   1],
            //        [125,  25,   5,   1]])
            // >>> cp.vander(x, increasing=True)
            // array([[  1,   1,   1,   1],
            //        [  1,   2,   4,   8],
            //        [  1,   3,   9,  27],
            //        [  1,   5,  25, 125]])
            // 

#if TODO
             given = x = cp.array({1, 2, 3, 5});
             given = cp.vander(x);
             expected =
                "array([[  1,   1,   1,   1],\n" +
                "       [  8,   4,   2,   1],\n" +
                "       [ 27,   9,   3,   1],\n" +
                "       [125,  25,   5,   1]])";
            Assert.AreEqual(expected, given.repr);
             given = cp.vander(x, increasing = True);
             expected =
                "array([[  1,   1,   1,   1],\n" +
                "       [  1,   2,   4,   8],\n" +
                "       [  1,   3,   9,  27],\n" +
                "       [  1,   5,  25, 125]])";
            Assert.AreEqual(expected, given.repr);
#endif
            // The determinant of a square Vandermonde matrix is the product
            // of the differences between the values of the input vector:

            // >>> cp.linalg.det(cp.vander(x))
            // 48.000000000000043
            // >>> (5-3)*(5-2)*(5-1)*(3-2)*(3-1)*(2-1)
            // 48
            // 

#if TODO
             given = cp.linalg.det(cp.vander(x));
             expected =
                "48.000000000000043";
            Assert.AreEqual(expected, given.repr);
             given = (5-3)*(5-2)*(5-1)*(3-2)*(3-1)*(2-1);
             expected =
                "48";
            Assert.AreEqual(expected, given.repr);
#endif
        }


