        [TestMethod]
        public void bmatTest()
        {
            // >>> A = cp.mat('1 1; 1 1')
            // >>> B = cp.mat('2 2; 2 2')
            // >>> C = cp.mat('3 4; 5 6')
            // >>> D = cp.mat('7 8; 9 0')
            // 

#if TODO
            var given = A = cp.mat('1 1; 1 1');
             given = B = cp.mat('2 2; 2 2');
             given = C = cp.mat('3 4; 5 6');
             given = D = cp.mat('7 8; 9 0');
#endif
            // All the following expressions construct the same block matrix:

            // >>> cp.bmat([[A, B], [C, D]])
            // matrix([[1, 1, 2, 2],
            //         [1, 1, 2, 2],
            //         [3, 4, 7, 8],
            //         [5, 6, 9, 0]])
            // >>> cp.bmat(cp.r_[cp.c_[A, B], cp.c_[C, D]])
            // matrix([[1, 1, 2, 2],
            //         [1, 1, 2, 2],
            //         [3, 4, 7, 8],
            //         [5, 6, 9, 0]])
            // >>> cp.bmat('A,B; C,D')
            // matrix([[1, 1, 2, 2],
            //         [1, 1, 2, 2],
            //         [3, 4, 7, 8],
            //         [5, 6, 9, 0]])
            // 

#if TODO
             given = cp.bmat({{A, B}, {C, D}});
            var expected =
                "matrix([[1, 1, 2, 2],\n" +
                "        [1, 1, 2, 2],\n" +
                "        [3, 4, 7, 8],\n" +
                "        [5, 6, 9, 0]])";
            Assert.AreEqual(expected, given.repr);
             given = cp.bmat(cp.r_{cp.c_{A, B}, cp.c_{C, D}});
             expected =
                "matrix([[1, 1, 2, 2],\n" +
                "        [1, 1, 2, 2],\n" +
                "        [3, 4, 7, 8],\n" +
                "        [5, 6, 9, 0]])";
            Assert.AreEqual(expected, given.repr);
             given = cp.bmat('A,B; C,D');
             expected =
                "matrix([[1, 1, 2, 2],\n" +
                "        [1, 1, 2, 2],\n" +
                "        [3, 4, 7, 8],\n" +
                "        [5, 6, 9, 0]])";
            Assert.AreEqual(expected, given.repr);
#endif
        }
