            // 

#if TODO
            var given = x = [1, 2, 3];
             given = y = [4, 5, 6];
             given = cp.cross(x, y);
            var expected =
                "array([-3,  6, -3])";
            Assert.AreEqual(expected, given.repr);
#endif
            // One vector with dimension 2.

            // >>> x = [1, 2]
            // >>> y = [4, 5, 6]
            // >>> cp.cross(x, y)
            // array([12, -6, -3])
            // 

#if TODO
             given = x = [1, 2];
             given = y = [4, 5, 6];
             given = cp.cross(x, y);
             expected =
                "array([12, -6, -3])";
            Assert.AreEqual(expected, given.repr);
#endif
            // Equivalently:

            // >>> x = [1, 2, 0]
            // >>> y = [4, 5, 6]
            // >>> cp.cross(x, y)
            // array([12, -6, -3])
            // 

#if TODO
             given = x = [1, 2, 0];
             given = y = [4, 5, 6];
             given = cp.cross(x, y);
             expected =
                "array([12, -6, -3])";
            Assert.AreEqual(expected, given.repr);
#endif
            // Both vectors with dimension 2.

            // >>> x = [1,2]
            // >>> y = [4,5]
            // >>> cp.cross(x, y)
            // -3
            // 

#if TODO
             given = x = [1,2];
             given = y = [4,5];
             given = cp.cross(x, y);
             expected =
                "-3";
            Assert.AreEqual(expected, given.repr);
#endif
            // Multiple vector cross-products. Note that the direction of the cross
            // product vector is defined by the right-hand rule.

            // >>> x = cp.array([[1,2,3], [4,5,6]])
            // >>> y = cp.array([[4,5,6], [1,2,3]])
            // >>> cp.cross(x, y)
            // array([[-3,  6, -3],
            //        [ 3, -6,  3]])
            // 

#if TODO
             given = x = cp.array({{1,2,3}, {4,5,6}});
